function validateform()
{
	var a=validatEmail();
	var b=validatPhone();
	var c=validatepass();
	var d=ValidateConPass();
	if(a==true && b==true && c==true && d==true)
	{
		return true;

	}
	return false;
}


function validatEmail(){

    var emailId = document.getElementById("emailid");
	var errorMail = document.getElementById("errormail");

	var val=emailId.value;

	 if (/^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/.test(val))
	 {
	 		errorMail.innerHTML="";
	 
	 return true;
	 }
	 else
	 {
	 	//alert("Enter a proper email id");
	 	errorMail.innerHTML="please enter valid email id ...!";
	  return false;
	 }	
	   
}
function validatPhone(){

    var phone = document.getElementById("phone");
	var error= document.getElementById("errorPhone");

	var val=phone.value;

	 if (/^\d{10}$/.test(val))
	 {
	 		error.innerHTML="";
	 
	 return true;
	 }
	 else
	 {

	//alert("Enter valid 10 digit number");
 	error.innerHTML="please enter 10 Digit Number ...!";
	 return false;
	 }	
	 
		
}

function validatepass(){

    var pass = document.getElementById("password");
	var error= document.getElementById("errorpass");

	var val=pass.value;

	 if (/^(?=.*?[A-Z])(?=.*?[a-z])(?=.*?[0-9])(?=.*?[^\w\s]).{8,}$/i.test(val))
	 {
	 		error.innerHTML="";
	 
	 return true;
	 }
	 else
	 {

	//alert("Enter valid 10 digit number");
 	error.innerHTML="Password should contain one special character to make password strong";
	 return false;
	 }	
	 
		
}

function ValidateConPass(){

	var pass = document.getElementById("password");  
    var conpass = document.getElementById("ConPassword");
	var error= document.getElementById("errorconpass");

	var val1=pass.value;
	var val2=conpass.value;

	 if (val1!==val2)
	 {
		error.innerHTML="Password not match";
	 	return false;
	 }
	 else
	 {
	 	 		error.innerHTML="";
	 	 				 return true;

 	 
	 }	
	 
		
}







